        // Time complexity: O(rows x columns)
        // Space complexity: O(rows x columns)
        public int[][] UpdateMatrix(int[][] mat)
        {
            Queue<(int r, int c)> q = new Queue<(int r, int c)>();

            //map = new int[mat.Length][mat[0].Length];
            var map = new int[mat.Length][];
            for (int r = 0; r < mat.Length; r++)
            {
                map[r] = new int[mat[0].Length];
                for (int c = 0; c < mat[0].Length; c++)
                {
                    if (mat[r][c] == 0)
                    {
                        // Add all zeros to the queue, they are our starting points
                        q.Enqueue((r, c));
                        map[r][c] = 0;
                    }
                    else
                    {
                        map[r][c] = Int32.MaxValue;
                    }
                }
            }

            // Easier to loop though the direction possibilities this way, left, right, up, down
            List<(int r, int c)> dir = new List<(int r, int c)>() { (1, 0), (-1, 0), (0, 1), (0, -1) };
            
            while (q.Count != 0)
            {
                var cell = q.Dequeue();
                for (int i = 0; i < dir.Count; i++)
                {
                    int newR = cell.r + dir[i].r;
                    int newC = cell.c + dir[i].c;
                    if (newR >= 0 && newC >= 0 && newR < map.Length && newC < map[0].Length)
                    {
                        // If this cell is of a lower value (distance to zero) than its neighbors, set
                        // the neighbors to this cell + 1, and add them to the queue so they can check their neighbors.
                        // Side benefit: This prevents infinite looping
                        if (map[cell.r][cell.c] + 1 < map[newR][newC])
                        {
                            // Set the neighbor, then queue them up
                            map[newR][newC] = map[cell.r][cell.c] + 1;
                            q.Enqueue((newR, newC));
                        }
                    }
                    
                }
            }

            return map;
        }

